import React from 'react';
import ReactDOM from 'react-dom/client';
import Loader from './loader.js'
import mainMenu from './mainMenu.js'
import './assets/css/bite-style.css'

const Content = (
<div className="BiteApp">
  <div className="appMenu">
    <div>Home</div>
    <div>Innovation</div>
    <div>Technology</div>
  </div>
  <div className="appScreen"> {mainMenu()}
    <div className="hero">
      <div className="heroLeft">
        <h1>Get Actual Results with <br /> Innovation + Technology</h1>
      </div>
      <div className="heroRight">
        <svg height="400" width="400" xmlns="http://www.w3.org/2000/svg">
           <polyline points="10,400 100,250 250,300, 360,50" fill="transparent" stroke="#8093F1" style={{strokeWidth:15}} />
           <polygon points="340,45 376,60 385,0" fill="#8093F1" />

  <path clip-path="url(#myClipPath)" className="chart" />
</svg>
      </div>
    </div>
  </div>
</div>
);
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {favoritecolor: <Loader/>};
  }
  componentDidMount() {
    setTimeout(() => {
      this.setState({favoritecolor: Content})
    }, 5000)
  }
  render() {
    return (
      <>{this.state.favoritecolor}</>
    );
  }
}

const wasPageRefreshed = sessionStorage.getItem('refreshToken') == 'true';
        const tabCount = Number(localStorage.getItem('tabCount'));
        const isDarkTheme = localStorage.getItem('isDarkTheme') === 'true';
        const isOsSettingDarkTheme = window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches;


        if(tabCount == 0 && !wasPageRefreshed){
          if (isOsSettingDarkTheme) {
            document.body.classList.add('B-dark-theme');
          } else {
            document.body.classList.remove('B-dark-theme');
          }
        } else {
          if (isDarkTheme) {
            document.body.classList.add('B-dark-theme');
          } else {
            document.body.classList.remove('B-dark-theme');
          }
        }

const preloader = ReactDOM.createRoot(document.getElementById('Bite'));
preloader.render(<App/>);
