import { SummaryData, OverviewData } from './data.js';
import './css/app.css';

function Info(props) {
  return (
    <div className=''>
      <div className=''>
        <div className='mx-flex'>
          <img src={props.icon} alt='fb logo' />
          <span className=''>{props.username}</span>
        </div>
        <div className=''>{props.number}</div>
        <p className=''>{props.param}</p>
        <div className='flex mx-auto'>
          <img src={props.caret} />
          <span className=''>{props.figure + ' Today'}</span>
        </div>
      </div>
    </div>
  )
}

function Today(props) {
  return (
    <div className=''>
      <div className=''>
        <div className='flex'>
          <span className=''>{props.param}</span>
          <img src={props.icon} alt='fb logo' />
          <div className=''>{props.number}</div>
          <div className='flex'>
            <img src={props.caret} />
            <span className=''>{props.figure + '%'}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

function Summary() {
  return (
    <div className=''>
      <div className=''>
        <Info
          icon={SummaryData[0].icon}
          username={SummaryData[0].username}
          number={SummaryData[0].number}
          param={SummaryData[0].param}
          caret={SummaryData[0].caret}
          figure={SummaryData[0].figure}
        />
      </div>
      <div className=''>
        <Info 
          icon={SummaryData[1].icon}
          username={SummaryData[1].username}
          number={SummaryData[1].number}
          param={SummaryData[1].param}
          caret={SummaryData[1].caret}
          figure={SummaryData[1].figure}
        />
      </div>
      <div className=''>
        <Info 
          icon={SummaryData[2].icon}
          username={SummaryData[2].username}
          number={SummaryData[2].number}
          param={SummaryData[2].param}
          caret={SummaryData[2].caret}
          figure={SummaryData[2].figure}
        />
      </div>
      <div className=''>
        <Info 
          icon={SummaryData[3].icon}
          username={SummaryData[3].username}
          number={SummaryData[3].number}
          param={SummaryData[3].param}
          caret={SummaryData[3].caret}
          figure={SummaryData[3].figure}
        />
      </div>

    </div>
  ); 
}

function Overview() {
  return (
    <div className=''>
      <div className=''>
        <Today
          param={OverviewData[0].param}
          icon={OverviewData[0].icon}
          number={OverviewData[0].number}
          caret={OverviewData[0].caret}
          figure={OverviewData[0].figure}
        />
      </div>
      <div className=''>
        <Today
          param={OverviewData[1].param}
          icon={OverviewData[1].icon}
          number={OverviewData[1].number}
          caret={OverviewData[1].caret}
          figure={OverviewData[1].figure}
        />
      </div>
      <div className=''>
        <Today
          param={OverviewData[2].param}
          icon={OverviewData[2].icon}
          number={OverviewData[2].number}
          caret={OverviewData[2].caret}
          figure={OverviewData[2].figure}
        />
      </div>
      <div className=''>
        <Today
          param={OverviewData[3].param}
          icon={OverviewData[3].icon}
          number={OverviewData[3].number}
          caret={OverviewData[3].caret}
          figure={OverviewData[3].figure}
        />
      </div>

    </div>
  ); 
}

function App() {
  return (
    <div>
      <header>
      <h1 className="text-center text-3xl font-bold underline">
      Hello world!
    </h1>
        <div className='underline bg-black'>
          <h1>Social Media Dashboard</h1>
          <p>Total Followers: 23,0004</p>
        </div>
        <div className='flex-1'>
          <p>Dark Mode</p>
          <button className=''></button>
        </div>
      </header>
      <div className=''>
        <Summary />
      </div>
      <div className=''>
        <h3>Overview - Today</h3>
        <Overview />
      </div>
    </div>
  );
}

export default App
