import React from 'react';
import ReactDOM from 'react-dom/client';
import Loader from './loader.js'

const Content = (<h1>Steady growth <br /> with Innovation + Technology </h1>);


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {favoritecolor: <Loader/>};
  }
  componentDidMount() {
    setTimeout(() => {
      this.setState({favoritecolor: Content})
    }, 5000)
  }
  render() {
    return (
      <>{this.state.favoritecolor}</>
    );
  }
}


const preloader = ReactDOM.createRoot(document.getElementById('Bite'));
preloader.render(<App/>);
