import React from 'react';
import ReactDOM from 'react-dom/client';
import Loader from './loader.js'
import mainMenu from './mainMenu.js'
import './assets/css/bite-style.css'
import logo from './gf.svg'
import noto_fg from './images/noto-fg-light.png'

const Content = (
<div className="BiteApp">
      <div className="appMenu">
           <div>Home</div>
           <div>Innovation</div>
           <div>Technology</div>
      </div>
      <div className="appScreen"> {mainMenu()}
          <div className="hero">
               <div className="heroLeft">
                    <h1>Noto: a typeface <br />for the world<span style={{color:'#8093f1'}}>.</span></h1>

                </div>
                <div className="heroRight">
                      <img id='#noto' src={noto_fg}/>
                </div>
            </div>

            <div className="afterHero">
               <h4> We believe technology should grow your business and make you money.</h4> 
               <p>Beyond design, we build products that <i style={{backgroundColor:'#9fddfe', padding:'0 5px'}}>understand</i> the psychology of your customers and speak uniquely with them.
               This significantly improves performance and conversions across all your digital footprints.
               </p>
           </div>

           <div className="2columns50">
               <div className="left">

               </div>
               <div className="right">

               </div>
           </div>
           //close app screen
   </div>
</div>
);

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {favoritecolor: <Loader/>};
  }
  componentDidMount() {
    setTimeout(() => {
      this.setState({favoritecolor: Content})
    }, 2000)
  }
  render() {
    return (
      <>{this.state.favoritecolor}</>
    );
  }
}

const preloader = ReactDOM.createRoot(document.getElementById('Noto'));
preloader.render(<App/>);
