import React from 'react';
import ReactDOM from 'react-dom/client';
import Loader from './loader.js'
import mainMenu from './mainMenu.js'
import './assets/css/bite-style.css'

const Content = (
<div className="BiteApp">
  <div className="appMenu">
    <div>Home</div>
    <div>Innovation</div>
    <div>Technology</div>
  </div>
  <div className="appScreen"> {mainMenu()}
    <div className="hero">
      <div className="heroLeft">
        <h1>Get Actual Results with <br /> Innovation + Technology<span style={{color:'#8093f1'}}>.</span></h1>
        <a href=""><button className="contactButton">I want results now &nbsp;&nbsp;<svg height="10" width="70" xmlns="http://www.w3.org/2000/svg">
          <path stroke="#000" strokeWidth="2" fill="none" className="buttonArrow" d="M0,5 L60,5 L60,0 L70,5 L60,10 L60,5"/></svg>
        </button></a>
      </div>
      <div className="heroRight">
        <svg height="400" width="400" xmlns="http://www.w3.org/2000/svg">
          <polyline  className="chart" fill="none" stroke="#8093F1" stroke-miterlimit="10" strokeWidth="15"   points="10,400 100,250 250,300 360,50" />
           <polygon points="340,45 376,60 385,0" fill="#8093F1" opacity="0"> 
             <animate attributeType="CSS" attributeName="opacity" from="0" to="1" begin="1.3s" dur="0.35s" fill="freeze"/>
           </polygon>
        </svg>
      </div>
    </div>
    <div className="afterHero">
      <h4> We believe technology should grow your business and make you money.</h4> 
      <p>Beyond design, we build products that <i style={{backgroundColor:'#9fddfe', padding:'0 5px'}}>understand</i> the psychology of your customers and speak uniquely with them;
      improving performance and conversions across all your digital footprints.</p>
    </div>
  </div>
</div>
);
class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {favoritecolor: <Loader/>};
  }
  componentDidMount() {
    setTimeout(() => {
      this.setState({favoritecolor: Content})
    }, 5000)
  }
  render() {
    return (
      <>{this.state.favoritecolor}</>
    );
  }
}

const preloader = ReactDOM.createRoot(document.getElementById('Bite'));
preloader.render(<App/>);
